import React, { Component } from 'react'
//import L2 from './l2';
class L1 extends Component {
    constructor(props){
        super(props);
        this.state = {
            person:[{name:a,age:21},{name:b,age:22},{name:c,age:23}],
        };
    }
  render() {
    return (
      <div>
        let persons;
        persons=this.state.person.map(
                                      function(p)(return "<li>" + {p.name} + "</li>" ;)
                                      );
        <div>{persons}</div>
      </div>
    )
  }
}
export default L1;
